import React from 'react';
import { Button, FormControlLabel, Switch, TextField } from '@mui/material';

function FormularioCadastro() {
  let nome = '';
  return (
    <form onSubmit={(event) => {
      console.log(nome);
      event.preventDefault();
    }}
    >
      <TextField
        value={nome}
        onChange={(event) => {
          nome = event.target.value;
        }}
        id='nome'
        label='Nome'
        variant='outlined'
        fullWidth
        margin='normal'
      />
      <TextField
        id='sobrenome'
        label='Sobrenome'
        variant='outlined'
        fullWidth
        margin='normal'
      />
      <TextField
        id='cpf'
        label='CPF'
        variant='outlined'
        fullWidth
        margin='normal'
      />

      <FormControlLabel
        label='Promoções'
        control={<Switch name='Promoções' defaultChecked color='primary' />}
      />
      <FormControlLabel
        label='Novidades'
        control={<Switch name='Novidades' defaultChecked color='primary' />}
      />

      <Button variant='contained' color='primary'>Cadastrar</Button>
    </form>
  );
}

export default FormularioCadastro;
