import { Container, Typography } from '@mui/material';
import React from 'react';
import './App.css';
import FormularioCadastro from './components/FormularioCadastro/FormularioCadastro';
import 'fontsource-roboto';

function App() {
  return (
    <Container component='article' maxWidth='sm'>
      <Typography variant='h3' align='center' component='h1'>Formulário de cadastro</Typography>
      <FormularioCadastro onSubmit={onSubmitForm} validarCPF={validarCPF} />
    </Container>
  );
}

function onSubmitForm(dados) {
  console.log(dados);
}

function validarCPF(cpf) {
  if (cpf.length !== 11) {
    return { errado: true, texto: 'CPF deve ter 11 dígitos.' };
  } else {
    return { errado: true, texto: '' };
  }
}

export default App;
